mapper.select:
SELECT -- orders表 orders.id as id, orders.company_id as company_id, orders.user_id as user_id, orders.order_state as order_state, orders.pay_mode as pay_mode, orders.total_price as total_price, orders.receipt_id as receipt_id, orders.location_id as location_id, orders.remark as remark, orders.express_no as express_no,
-- user表 company.id as company_id, company.username as company_name, company.phone as company_phone, company.mail as company_mail, company.licence_pic as company_licence_pic, company.register_time as company_register_time,
-- user表 buyer.id as buyer_id, buyer.username as buyer_name, buyer.phone as buyer_phone, buyer.mail as buyer_mail,
-- product_order表 product_order.order_id as product_order_order_id, product_order.counts as product_order_count,
-- product表 product.id as product_id, product.prod_name as product_prodName, product.market_price as product_market_price, product.shop_price as product_shop_price, product.stock as product_stock, product.sales as product_sales, product.weight as product_weight,
-- prod_image表 image.prod_image_url as image_url, image.id as image_id,
-- order_state_time表 order_state_time.order_state as order_state_time_order_state, order_state_time.times as order_state_time_time,
-- receipt表 receipt.id as receipt_id, receipt.receipt_type as receipt_type, receipt.title as receipt_title, receipt.taxpayer_no as receipt_taxpayer_no, receipt.content as receipt_content,
-- location表 location.id as location_id, location.location as location, location.name as location_name, location.phone as location_phone, location.post_code as location_post_code
FROM gaoxi.orders as orders LEFT JOIN gaoxi.sys_user as company ON orders.company_id=company.id LEFT JOIN gaoxi.sys_user as buyer ON orders.user_id=buyer.id LEFT JOIN gaoxi.product_order as product_order ON product_order.order_id=orders.id LEFT JOIN gaoxi.product as product ON product.id=product_order.product_id LEFT JOIN gaoxi.order_state_time as order_state_time ON order_state_time.order_id=orders.id LEFT JOIN gaoxi.receipt as receipt ON receipt.id=orders.receipt_id LEFT JOIN gaoxi.location as location ON location.id=orders.location_id LEFT JOIN gaoxi.prod_image as image ON image.product_id=product.id
AND orders.id = #{orderQueryReq.id}
AND buyer_name like CONCAT('%',#{userQueryReq.buyer.username},'%')
AND buyer_mail = #{userQueryReq.buyer.mail}
AND company_name like CONCAT('%',#{userQueryReq.company.username},'%')
AND company_phone = #{userQueryReq.company.phone}
AND company_mail = #{userQueryReq.company.mail}
AND order_state = #{userQueryReq.orderStateCode}
AND location_name like CONCAT('%',#{userQueryReq.recipientName},'%')
AND location_phone like CONCAT('%',#{userQueryReq.recipientPhone},'%')
AND location like CONCAT('%',#{userQueryReq.recipientLocation},'%')
AND pay_mode = #{userQueryReq.payModeCode}
AND express_no = #{userQueryReq.expressNo}
AND order_state_time_order_state = 0 AND order_state_time_time > #{orderQueryReq.placeOrderStartTime}
AND order_state_time_order_state = 0 AND order_state_time_time < #{orderQueryReq.placeOrderEndTime}
AND buyer.id = #{orderQueryReq.buyerId}
AND company_id = #{orderQueryReq.sellerId}

mapper.update:
UPDATE gaoxi.orders

  SET order_state = #{ordersEntity.orderStateEnum.code}


    SET express_no = #{ordersEntity.expressNo}

WHERE id = #{ordersEntity.id}

mapper.delete:
DELETE FROM gaoxi.order_state_time
WHERE order_id = #{orderStateTimeEntity.orderId} AND
order_state = #{orderStateTimeEntity.orderStateEnum.code}

mapper.insert:
INSERT INTO gaoxi.orders
(id, user_id, company_id, order_state, pay_mode, total_price, receipt_id, location_id, remark, express_no)
VALUES
(#{ordersEntity.id},#{ordersEntity.buyer.id},#{ordersEntity.company.id},#{ordersEntity.orderStateEnum.code},#{ordersEntity.payModeEnum.code}
,#{ordersEntity.totalPrice},#{ordersEntity.receiptEntity.id},#{ordersEntity.locationEntity.id}
,#{ordersEntity.remark},#{ordersEntity.remark})
